import React, { useEffect, useState, useRef } from 'react';
import { useParams } from 'react-router-dom';
import { Card, CardContent, Typography, Button } from '@mui/material';

function PersonDetailsPage() {
  const { id } = useParams();
  const [person, setPerson] = useState(null);
  const [error, setError] = useState(null);
  const printRef = useRef(null); // Reference for the section to print

  const fetchPersonData = async () => {
    try {
      const response = await fetch(`http://localhost:5000/get-person/${id}`);
      if (response.ok) {
        const data = await response.json();
        setPerson(data);
        setError(null);
      } else {
        setError('Person not found');
      }
    } catch (error) {
      setError('Failed to retrieve person data. Please try again later.');
    }
  };

  const handlePrint = () => {
    if (printRef.current) {
      const printContents = printRef.current.innerHTML;
      const originalContents = document.body.innerHTML;
      document.body.innerHTML = printContents;
      window.print(); // Trigger the print action
      document.body.innerHTML = originalContents; // Restore original content
    }
  };

  useEffect(() => {
    fetchPersonData(); // Fetch person data when component mounts
  }, [id]);

  if (error) {
    return (
      <Card sx={{ maxWidth: 600, margin: '0 auto', marginTop: 10 }}>
        <CardContent>
          <Typography variant="body1" color="error">
            {error}
          </Typography>
        </CardContent>
      </Card>
    );
  }

  if (!person) {
    return (
      <Card sx={{ maxWidth: 600, margin: '0 auto', marginTop: 10 }}>
        <CardContent>
          <Typography variant="body1">Loading...</Typography>
        </CardContent>
      </Card>
    );
  }

  return (
    <div>
      <Card sx={{ maxWidth: 600, margin: '0 auto', marginTop: 10 }} ref={printRef}>
        <CardContent>
          <center>
            <Typography variant="h5" gutterBottom>
              Person Details
            </Typography>
          </center>
          {person.photo && ( // Display the photo if available
            <center>
              <img
                src={person.photo}
                alt={`Photo of ${person.fullName}`}
                style={{ maxWidth: '100%', borderRadius: '8px', marginBottom: '10px' }}
              />
            </center>
          )}
          <Typography variant="body1">Name: {person.fullName}</Typography>
          <Typography variant="body1">Age: {person.age}</Typography>
          <Typography variant="body1">Email: {person.email}</Typography>
          <Typography variant="body1">
            Father's Name: {person.fatherName}
          </Typography>
          <Typography variant="body1">
            Father's Occupation: {person.fatherOccupation}
          </Typography>
          <Typography variant="body1">Mother's Name: {person.motherName}</Typography>
          <Typography variant="body1">
            Mother's Occupation: {person.motherOccupation}
          </Typography>
          <Typography variant="body1">Phone Number: {person.phoneNumber}</Typography>
          <Typography variant="body1">Address: {person.address}</Typography>
          <Typography variant="body1">City: {person.city}</Typography>
          <Typography variant="body1">State: {person.state}</Typography>
          <Typography variant="body1">Country: {person.country}</Typography>
          <Typography variant="body1">Pincode: {person.pincode}</Typography>
          <Typography variant="body1">Guardian's Name: {person.guardianName}</Typography>
          <Typography variant="body1">Guardian's Address: {person.guardianAddress}</Typography>
          <Typography variant="body1">Previous School Name: {person.previousSchoolName}</Typography>
          <Typography variant="body1">Last Studied City: {person.lastStudiedCity}</Typography>
          <Typography variant="body1">Last Studied State: {person.lastStudiedState}</Typography>
          <Typography variant="body1">TC Number: {person.tcNumber}</Typography>
          <Typography variant="body1">Year of Study: {person.yearOfStudy}</Typography>
          <Typography variant="body1">Identification Marks: {person.identificationMarks}</Typography>
        </CardContent>
      </Card>
      <center>
        <Button variant="contained" color="primary" onClick={handlePrint}>
          Print
        </Button>
      </center>
    </div>
  );
}

export default PersonDetailsPage;
