import React, { useState } from 'react';
import axios from 'axios';

const SearchStudent = () => {
    const [admissionNumber, setAdmissionNumber] = useState('');
    const [studentData, setStudentData] = useState(null);
    const [error, setError] = useState(null);

    const handleSearch = async () => {
        try {
            const response = await axios.get(`/api/students/${admissionNumber}`);
            setStudentData(response.data);
            setError(null); // Clear any previous errors
        } catch (error) {
            console.error(error);
            setError('Error fetching student data');
            setStudentData(null); // Clear existing student data on error
        }
    };

    return (
        <div>
            <input
                type="text"
                value={admissionNumber}
                onChange={(e) => setAdmissionNumber(e.target.value)}
                placeholder="Enter Admission Number"
            />
            <button onClick={handleSearch}>Search</button>
            {error && <p>{error}</p>}
            {studentData && (
                <div>
                    <h2>Student Details</h2>
                    <p>Name: {studentData.studentName}</p>
                    <p>Gender: {studentData.gender}</p>
                    {/* Display other student details */}
                </div>
            )}
        </div>
    );
};

export default SearchStudent;
