import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Card, CardContent, Typography, CircularProgress, Alert } from '@mui/material'; // Added Alert for error messages

const StudentDetails = () => {
  const { admissionNumber } = useParams(); // Get admission number from route params
  const [studentData, setStudentData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStudentData = async () => {
      try {
        const response = await fetch(`http://localhost:3001/student/${admissionNumber}`);
        if (response.ok) {
          const data = await response.json();
          setStudentData(data); // Store the student and certificate data
          setError(null); // Clear error if successful
        } else if (response.status === 404) {
          setError('Student not found.'); // Handle 404 errors
        } else {
          setError('Error retrieving student data.'); // Handle other errors
        }
      } catch (err) {
        setError('An error occurred while retrieving student data.'); // Handle fetch errors
      } finally {
        setLoading(false); // Stop the loading indicator
      }
    };

    fetchStudentData(); // Fetch data when admission number changes
  }, [admissionNumber]); // Dependency array ensures data is fetched when admission number changes

  if (loading) {
    return (
      <div style={{ textAlign: 'center', marginTop: 20 }}>
        <CircularProgress /> {/* Display a loading spinner */}
      </div>
    );
  }

  if (error) {
    return (
      <Card sx={{ maxWidth: 600, margin: '0 auto', marginTop: 20 }}>
        <CardContent>
          <Alert severity="error">{error}</Alert> {/* Display error messages with an alert */}
        </CardContent>
      </Card>
    );
  }

  if (!studentData) {
    return (
      <Card sx={{ maxWidth: 600, margin: '0 auto', marginTop: 20 }}>
        <CardContent>
          <Typography variant="h5" color="error">
            No student data found.
          </Typography>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card sx={{ maxWidth: 600, margin: '0 auto', marginTop: 20 }}>
      <CardContent>
        <Typography variant="h5">Student Information</Typography>
        <Typography><strong>Full Name:</strong> {studentData.student.fullName}</Typography>
        <Typography><strong>Gender:</strong> {studentData.student.gender}</Typography>
        <Typography><strong>Age:</strong> {studentData.student.age}</Typography>

        <Typography variant="h5" style={{ marginTop: 20 }}>Certificates</Typography>
        {studentData.certificates.map((cert, index) => (
          <div key={index}>
            <Typography variant="body1"><strong>Certificate {index + 1}</strong></Typography>
            {cert.photo && (
              <img src={cert.photo} alt="Certificate Photo" style={{ width: '200px', height: '200px' }} />
            )}
          </div>
        ))}
      </CardContent>
    </Card>
  );
};

export default StudentDetails; // Ensure the correct export name
