import React, { useState } from 'react';
import { Card, CardContent, Typography, Button, TextField, CircularProgress } from '@mui/material';

const ViewStudent = () => {
  const [admissionNumber, setAdmissionNumber] = useState('');
  const [studentData, setStudentData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = setError('');

  const handleChange = (e) => {
    setAdmissionNumber(e.target.value);
    if (error) setError(null);
  };

  const fetchStudentData = async (admissionNumber) => {
    setLoading(true);
    setError(null);
    setStudentData(null);

    try {
      const response = await fetch(`http://localhost:5000/view-student/${admissionNumber}`);

      if (response.ok) {
        const data = await response.json();
        setStudentData(data);
      } else {
        setError('Student not found.');
      }
    } catch (err) {
      setError('An error occurred while fetching data. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!admissionNumber.trim()) {
      setError('Admission number is required.');
      return;
    }

    fetchStudentData(admissionNumber);
  };

  return (
    <Card sx={{ maxWidth: 600, margin: '0 auto', marginTop: 20 }}>
      <CardContent>
        <center>
          <Typography variant="h5">View Student Information</Typography>
        </center>
        <form onSubmit={handleSubmit}>
          <TextField
            fullWidth
            label="Admission Number"
            value={admissionNumber}
            onChange={handleChange}
            variant="outlined"
            margin="normal"
            placeholder="Enter Admission Number"
          />
          <center>
            <Button type="submit" variant="contained" color="primary" disabled={loading}>
              {loading ? <CircularProgress size={24} /> : 'Search'}
            </Button>
          </center>
        </form>

        {error && (
          <Typography variant="body1" color="error" style={{ marginTop: 20 }}>
            {error}
          </Typography>
        )}

        {studentData && (
          <div style={{ marginTop: 20 }}>
            <Typography variant="h6">Student Information</Typography>
            <Typography><strong>Full Name:</strong> {studentData.fullName}</Typography>
            <Typography><strong>Gender:</strong> {studentData.gender}</Typography>
            <Typography><strong>Age:</strong> {studentData.age}</Typography>
            <Typography><strong>Father's Name:</strong> {studentData.fatherName}</Typography>
            <Typography><strong>Mother's Name:</strong> {studentData.motherName}</Typography>
            <Typography><strong>Father's Occupation:</strong> {studentData.fatherOccupation}</Typography>
            <Typography><strong>Mother's Occupation:</strong> {studentData.motherOccupation}</Typography>
            <Typography><strong>Ration Card Number:</strong> {studentData.rationCardNumber}</Typography>
            <Typography><strong>Phone Number:</strong> {studentData.phoneNumber}</Typography>
            <Typography><strong>Address:</strong> {studentData.address}</Typography>
            <Typography><strong>City:</strong> {studentData.city}</Typography>
            <Typography><strong>State:</strong> {studentData.state}</Typography>
            <Typography><strong>Country:</strong> {studentData.country}</Typography>
            <Typography><strong>Pincode:</strong> {studentData.pincode}</Typography>
            <Typography><strong>Guardian's Name:</strong> {studentData.guardianName}</Typography>
            <Typography><strong>Guardian's Address:</strong> {studentData.guardianAddress}</Typography>
            <Typography><strong>Previous School Name:</strong> {studentData.previousSchoolName}</Typography>
            <Typography><strong>Last Studied City:</strong> {studentData.lastStudiedCity}</Typography>
            <Typography><strong>Last Studied State:</strong> {studentData.lastStudiedState}</Typography>
            <Typography><strong>TC Number:</strong> {studentData.tcNumber}</Typography>
            <Typography><strong>Year of Study:</strong> {studentData.yearOfStudy}</Typography>
            <Typography><strong>Identification Marks:</strong> {studentData.identificationMarks}</Typography>
          </div>
        )}
      </CardContent>
    </Card>
  );
};

export default ViewStudent;
