// GetStudentByAdmissionNumberForm.js
import React, { useState } from 'react';
import { Card, CardContent, Typography, TextField, Button } from '@mui/material';
import { Link, useNavigate } from 'react-router-dom';

const GetStudentByAdmissionNumberForm = () => {
  const [admissionNumber, setAdmissionNumber] = useState('');
  const navigate = useNavigate(); // For page navigation
  const [error, setError] = useState(null);

  const handleChange = (event) => {
    setAdmissionNumber(event.target.value); // Update admission number
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch(`http://localhost:3001/PersonDetails/${admissionNumber}`);
      if (response.ok) {
        // Navigate to the details page with the admission number
        navigate(`/PersonDetailsPage/${admissionNumber}`);
        setError(null);
      } else {
        setError('Student not found'); // Set error message
      }
    } catch (error) {
      setError('Failed to retrieve student data. Please try again.');
    }
  };

  return (
    <Card sx={{ maxWidth: 600, margin: '0 auto', marginTop: 20 }}>
      <CardContent>
        <center>
          <Typography variant="h5" gutterBottom>
            Enter Admission Number
          </Typography>
        </center>
        <form onSubmit={handleSubmit}>
          <TextField
            fullWidth
            label="Admission Number"
            value={admissionNumber}
            onChange={handleChange}
            placeholder="Enter Admission Number"
            variant="outlined"
            margin="normal"
          />
          <center>
            <Button type="submit" variant="contained" color="primary">
              Search
            </Button>
          </center>
        </form>
        {error && (
          <Typography variant="body1" color="error" style={{ marginTop: 20 }}>
            {error}
          </Typography>
        )}
      </CardContent>
    </Card>
  );
};

export default GetStudentByAdmissionNumberForm;
