import React, { useState } from 'react';
import { Card, CardContent, Typography, TextField, Button } from '@mui/material';
import { Link, useNavigate } from 'react-router-dom'; // To navigate to another page

function GetPersonByIdForm() {
  const [id, setId] = useState('');
  const navigate = useNavigate(); // Create a navigate instance
  const [error, setError] = useState(null);

  const handleChange = (event) => {
    setId(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch(`http://localhost:5000/get-person/${id}`);
      if (response.ok) {
        // If successful, navigate to the new page with the person's ID
        navigate(`/person/${id}`);
        setError(null);
      } else {
        setError('Person not found');
      }
    } catch (error) {
      setError('Failed to retrieve person data. Please try again.');
    }
  };

  return (<> 
  <Link to='/dashboard'><Typography>back to dashboard</Typography></Link>
    <Card sx={{ maxWidth: 600, margin: '0 auto', marginTop: 20 }}>
      <CardContent>
        <center><Typography variant="h5" gutterBottom>
          Enter the admission number to view the details
        </Typography></center>
        <form onSubmit={handleSubmit}>
          <TextField
            fullWidth
            label="ID"
            value={id}
            onChange={handleChange}
            placeholder="Enter ID"
            variant="outlined"
            margin="normal"
          />
          <center><Button type="submit" variant="contained" color="primary">
            Search
          </Button></center>
        </form>
        {error && (
          <Typography variant="body1" color="error" style={{ marginTop: 20 }}>
            {error}
          </Typography>
        )}
      </CardContent>
    </Card>
    </>);
}

export default GetPersonByIdForm;
