import React, { useState } from 'react';
import { Card, CardContent, Typography, TextField, Button, CircularProgress } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const GetStudentByAdmissionNumberForm = () => {
  const [admissionNumber, setAdmissionNumber] = useState('');
  const [loading, setLoading] = useState(false); // Loading indicator
  const [error, setError] = useState(null);
  const navigate = useNavigate(); // For page navigation

  const handleChange = (event) => {
    setAdmissionNumber(event.target.value);
    if (error) {
      setError(null); // Reset error when user inputs a value
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!admissionNumber.trim()) { // Ensure it's not just empty or whitespace
      setError('Admission number cannot be empty');
      return;
    }

    setLoading(true); // Start loading indicator
    try {
      const response = await fetch(`http://localhost:5000/Pers/${admissionNumber}`, { timeout: 5000 }); // Timeout
      if (response.ok) {
        navigate(`/PersonDetailsPage/${admissionNumber}`); // Navigate on success
        setError(null); // Clear error
      } else if (response.status === 404) {
        setError('Student not found'); // Handle 404
      } else {
        setError('Error retrieving student data.'); // Other errors
      }
    } catch (error) {
      setError('Failed to retrieve student data. Please try again.'); // Network error
    } finally {
      setLoading(false); // Stop loading indicator
    }
  };

  return (
    <Card sx={{ maxWidth: 600, margin: '0 auto', marginTop: 20 }}>
      <CardContent>
        <center>
          <Typography variant="h5" gutterBottom>
            Enter Admission Number
          </Typography>
        </center>
        <form onSubmit={handleSubmit}>
          <TextField
            fullWidth
            label="Admission Number"
            value={admissionNumber}
            onChange={handleChange}
            placeholder="Enter Admission Number"
            variant="outlined"
            margin="normal"
          />
          <center>
            <Button type="submit" variant="contained" color="primary" disabled={loading}>
              {loading ? <CircularProgress size={24} /> : 'Search'}
            </Button>
          </center>
        </form>
        {error && (
          <Typography variant="body1" color="error" style={{ marginTop: 20 }}>
            {error}
          </Typography>
        )}
      </CardContent>
    </Card>
  );
};

export default GetStudentByAdmissionNumberForm;
