import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Card, CardContent, Typography, Button } from '@mui/material'; // Import Button for print action
import { useRef } from 'react'; // Import useRef to target the section to print

function PersonDetailsPage() {
  const { id } = useParams(); // Get the ID from the URL
  const [person, setPerson] = useState(null);
  const [error, setError] = useState(null);

  // Use useRef to refer to the part of the component to print
  const printRef = useRef(null);

  const handlePrint = () => {
    if (printRef.current) {
      // This triggers the print dialog for the referenced part
      const printContents = printRef.current.innerHTML; // Get the inner HTML of the part to print
      const originalContents = document.body.innerHTML; // Save the original content of the page

      document.body.innerHTML = printContents; // Set the body content to the part to print
      window.print(); // Trigger the print action
      document.body.innerHTML = originalContents; // Restore the original content
    }
  };

  useEffect(() => {
    const fetchPerson = async () => {
      try {
        const response = await fetch(`http://localhost:5000/get-person/${id}`);
        if (response.ok) {
          const data = await response.json();
          setPerson(data);
          setError(null);
        } else {
          setError('Person not found');
        }
      } catch (error) {
        setError('Failed to retrieve person data.');
      }
    };

    fetchPerson(); // Fetch person data when component mounts
  }, [id]);

  if (error) {
    return (
      <Card sx={{ maxWidth: 600, margin: '0 auto', marginTop: 10 }}>
        <CardContent>
          <Typography variant="body1" color="error">
            {error}
          </Typography>
        </CardContent>
      </Card>
    );
  }

  if (!person) {
    return (
      <Card sx={{ maxWidth: 600, margin: '0 auto', marginTop: 10 }}>
        <CardContent>
          <Typography variant="body1">
            Loading...
          </Typography>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card sx={{ maxWidth: 600, margin: '0 auto', marginTop: 5 }}>
      <CardContent ref={printRef}> {/* This ref is the part to print */}
        <center><Typography variant="h5" gutterBottom>
          Person Details
        </Typography></center>
        <Typography>Name: {person.fullName}</Typography>
        <Typography>Age: {person.age}</Typography>
        <Typography>Email: {person.email}</Typography>
        <Typography>Father's Name: {person.fatherName}</Typography>
        <Typography>Father's Occupation: {person.fatherOccupation}</Typography>
        <Typography>Mother's Name: {person.motherName}</Typography>
        <Typography>Mother's Occupation: {person.motherOccupation}</Typography>
        <Typography>Ration Card Number: {person.rationCardNumber}</Typography>
        <Typography>Phone Number: {person.phoneNumber}</Typography>
        <Typography>Caste: {person.caste}</Typography>
        <Typography>Religion: {person.religion}</Typography>
        <Typography>Address: {person.address}</Typography>
        <Typography>City: {person.city}</Typography>
        <Typography>State: {person.state}</Typography>
        <Typography>Country: {person.country}</Typography>
        <Typography>Pincode: {person.pincode}</Typography>
        <Typography>Guardian's Name: {person.guardianName}</Typography>
        <Typography>Guardian's Address: {person.guardianAddress}</Typography>
        <Typography>Previous School Name: {person.previousSchoolName}</Typography>
        <Typography>Last Studied City: {person.lastStudiedCity}</Typography>
        <Typography>Last Studied State: {person.lastStudiedState}</Typography>
        <Typography>TC Number: {person.tcNumber}</Typography>
        <Typography>Year of Study: {person.yearOfStudy}</Typography>
        <Typography>Identification Marks: {person.identificationMarks}</Typography>
      </CardContent>
      {/* Add the Print Button */}
      <center><Button variant="contained" color="primary" onClick={handlePrint}>
        Print
      </Button></center>
    </Card>
  );
}

export default PersonDetailsPage;
