import React, { useState } from 'react';

function GetPersonByIdForm() {
  const [id, setId] = useState('');
  const [person, setPerson] = useState(null);

  const handleChange = (event) => {
    setId(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch(`http://localhost:5000/get-person/${id}`);
      if (response.ok) {
        const data = await response.json();
        setPerson(data);
      } else {
        alert('Person not found');
      }
    } catch (error) {
      console.error('Error retrieving person:', error);
      alert('Failed to retrieve person data. Please try again.');
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={id}
          onChange={handleChange}
          placeholder="Enter ID"
        />
        <button type="submit">Search</button>
      </form>
      {person && (
        <div>
          <h2>Person Details</h2>
          <p>ID: {person.id}</p>
          <p>Name: {person.name}</p>
          <p>Age: {person.age}</p>
          <p>Email: {person.email}</p>
          <p>Father's Name: {person.father_name}</p>
          <p>Mother's Name: {person.mother_name}</p>
        </div>
      )}
    </div>
  );
}

export default GetPersonByIdForm;
